<?php

namespace C2J\EasySaisieBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * StudentPromotionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class StudentPromotionRepository extends EntityRepository
{
	public function findAllPromotionsByYearDistinct() 
	{
		$qb = $this ->createQueryBuilder('sp')
					->join('sp.promotion', 'p')
					->addSelect('p')
					->groupBy('p.name')
					->orderBy('sp.year', 'DESC')
					->addOrderBy('p.name', 'ASC');

	    // $qb = $this ->createQueryBuilder('sp')
	    //             ->select('p.name')
	    //             ->distinct()
	    //             ->addSelect('sp.id')
	    //             ->addSelect('sp.year')
	    //             ->join('sp.promotion', 'p')
	    //             ->orderBy('sp.year', 'DESC')
	    //             ->addOrderBy('p.name', 'ASC');

		return $qb 	->getQuery()
					->getResult();
	}

	public function findAllMarksByStudentsByPromotion($promotion_id, $year) 
	{
		return $this->createQueryBuilder('s')
					->join('sp.student', 's')
						->addSelect('s')
					->join('m.teachingUnitSubject', 'tus')
						->addSelect('tus')
					->join('tus.subject', 'sub')
						->addSelect('sub')
					->join('tus.teachingUnit', 'tu')
						->addSelect('tu')
					->join('tu.container', 'c')
						->addSelect('c')
					->join('m.studentPromotion', 'sp')
						->addSelect('sp')					
					->join('sp.promotion', 'p')
						->addSelect('p')

					->where('sp.promotion = :promotion_id')
						->setParameter('promotion_id', $promotion_id)
					->andWhere('sp.year = :year')
						->setParameter('year', $year)
					->getQuery()
					->getResult();
	}
}
